import React from "react";
import { Link, redirect } from "react-router-dom";
import "./NavBar.css";

class NavBar extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            linksArray: []
        }
    }

    componentDidMount() {
        const numLinks = document.getElementById("linksContainer").children.length;
        let newLinksArray = []
        for (let i = 0; i < numLinks; i++) {
            newLinksArray[i] = false;
        }

        this.setState({
            linksArray: newLinksArray
        });
    }

    setLinkActive(index) {
        const numLinks = document.getElementById("linksContainer").children.length;
        let newLinksArray = []
        for (let i = 0; i < numLinks; i++) {
            if (i == index) {
                newLinksArray[i] = true;
            }
            else {
                newLinksArray[i] = false;
            }
        }

        this.setState({
            linksArray: newLinksArray
        });
    }

    render() {
        return (
            <div id="navBarContainer">
                <div id="linksContainer">
                    <Link to="/" className={`${ this.state.linksArray[0] ? "active" : ""} navBarLink`} onClick={() => this.setLinkActive(0)}>Home</Link>
                    <Link to="/projects" className={`${ this.state.linksArray[1] ? "active" : ""} navBarLink`} onClick={() => this.setLinkActive(1)}>Projects</Link>
                </div>
            </div>
        );
    }
}

export default NavBar;
