import React from "react";
import { redirect } from "react-router-dom";
import "./NavBar.css";

class NavBar extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            linksArray: []
        }
    }

    componentDidMount() {
        const numLinks = Document.getElementById("linksContainer").children.length;
        let newLinksArray = []
        for (let i = 0; i < numLinks; i++) {
            newLinksArray[i] = false;
        }

        this.setState({
            linksArray: newLinksArray
        });
    }

    render() {
        return (
            <div id="navBarContainer">
                <div id="linksContainer">
                    <a className={`${active ? "" : ""}`} onClick={redirect("/projects")}>Projects</a>
                </div>
            </div>
        );
    }
}

export default NavBar;
